		$res = "";
		for ($i=0; $i<$imax; $i++) {
			$char_val = substr($cad, $i, 1);
			$char_num = ord($char_val);
			$char_valid = false;

			if ($char_num >= ord("0") && $char_num <= ord("9")) {
				$char_valid = true;
			}


			if ($char_num >= ord("a") && $char_num <= ord("z")) {
				$char_valid = true;
			}


			if ($char_num >= ord("A") && $char_num <= ord("Z")) {
				$char_valid = true;
			}

			if ($char_val==".") $char_valid = true;
			if ($char_val=="_") $char_valid = true;
			if ($char_val=="(") $char_valid = true;
			if ($char_val==")") $char_valid = true;

			if ($char_valid==true) {
				$res .= $char_val;
			}
		}
		return $res;
	}

	public function download() {
